import { createSlice, nanoid } from "@reduxjs/toolkit";

const initialState = [
  {
    id: "1",
    taskName: "the first todo task!",
    isChecked: false,
  },
  {
    id: "2",
    taskName: "the second todo task!",
    isChecked: false,
  },
  {
    id: "3",
    taskName: "the finished todo task!",
    isChecked: true,
  },
];
const todoTasksSlice = createSlice({
  name: "tasks",
  initialState,
  reducers: {
    addTask: {
      reducer(state, action) {
        state.push(action.payload);
      },
      prepare(taskName) {
        return {
          payload: {
            id: nanoid(),
            taskName,
            isChecked: false,
          },
        };
      },
    },
    checkedTask(state, action) {
      const { id, changedChecked } = action.payload;
      return {
        ...state,
        id: id,
        isChecked: changedChecked,
        taskName: state.taskName,
      };
      // state.isChecked = changedChecked;
      // state.id = id;
    },
    deleteTask(state) {},
  },
});

export const { addTask, checkedTask, deleteTask } = todoTasksSlice.actions;
export default todoTasksSlice.reducer;
