import { useSelector, useDispatch } from "react-redux";
import React from "react";
import { checkedTask } from "./todoTasksSlice";

const CompletedList = () => {
  const todo = useSelector((state) =>
    state.tasks.filter((task) => task.isChecked)
  );
  const dispatch = useDispatch();

  const handleClick = () => {
    if (isChecked) {
      dispatch(checkedTask());
    }
  };

  const render = todo.map((task) => (
    <section key={task.id}>
      <label htmlFor="check-box" />
      <input className="check-box" type="checkbox" onClick={handleClick} />
      <p className={task.id}>{task.taskName}</p>
    </section>
  ));
  return (
    <section>
      <h3>Completed</h3>
      {render}
    </section>
  );
};
export default CompletedList;
