import classes from "./Header.module.css";
import { useDispatch } from "react-redux";
import { taskActions } from "./todoTasksSlice";

const Header = () => {
  const dispatch = useDispatch();
  const handleAddTasks = () => {
    dispatch(taskActions.addTask);
  };

  return (
    <header>
      <h1>Use this to manage your life and work</h1>
      <label htmlFor="add-task" />
      <input type="text" className="add-task" />
      <button className="add-btn" onClick={handleAddTasks}>
        +
      </button>
    </header>
  );
};

export default Header;
